package cn.com.qs;import org.apache.commons.io.FileUtils;import java.io.File;import java.io.FileOutputStream;import java.io.IOException;/** * Hello world! * */public class App {    public static void main( String[] args ) throws IOException {       File fold=new File("D:\\七月\\第20课 采样与变分\\采样与变分");        String prefix="f9163ad9.m3u8";        int maxNum=10000;        File output = new File(fold.getParentFile(), "采样与变分.ts");        FileOutputStream fos = new FileOutputStream(output);        for(int ri=0;ri<=maxNum;ri++){            File source=new File(fold,prefix+"."+ri+".ts");            if(source.exists()){                FileUtils.copyFile(source,fos);            }else{                break;            }        }        fos.flush();        fos.close();    }}